    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         l1=Integer.parseInt(tfBase.getText());
         l2=Integer.parseInt(tfLado.getText());
         alt=Integer.parseInt(tfAlt.getText());
         
         if(l1==l2 && l1==alt){
             jLabel7.setText("Equilátero");
         }else if(l1 != l2 && l1!= alt && l2!= alt){
              jLabel7.setText("Escaleno");
         }else{
             jLabel7.setText("Isósceles");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

