    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        l1=Integer.parseInt(tfAng1.getText());
         l2=Integer.parseInt(tfAng2.getText());
         alt=Integer.parseInt(tfAng3.getText());
         
         if((l1+l2+alt)==180){
         if(l1==l2 && l1==alt){
             jLabel9.setText("Equilátero");
         }else if(l1 != l2 && l1!= alt && l2!= alt){
              jLabel9.setText("Escaleno");
         }else{
             jLabel9.setText("Isósceles");
         }
        }else
             jLabel9.setText("Não é triangulo");
    }//GEN-LAST:event_jButton2ActionPerformed

